  ];

  // Mock promotions
  const promotions = [
    {
      title: 'Send Money & Win!',
      description: 'Send money 5 times this month and get a chance to win ₦50,000',
      badge: 'Limited Time',
      color: 'bg-gradient-to-r from-red-500 to-pink-500'
    },
    {
      title: 'Zero Fees Weekend',
      description: 'Enjoy zero transaction fees on all money transfers this weekend',
      badge: 'This Weekend',
      color: 'bg-gradient-to-r from-blue-500 to-purple-500'
    },
    {
      title: 'Refer & Earn',
      description: 'Invite friends and earn ₦1,000 for each successful referral',
      badge: 'Ongoing',
      color: 'bg-gradient-to-r from-green-500 to-teal-500'
    },
  ];

  const nextPromo = () => {
    setCurrentPromoIndex((prev) => (prev + 1) % promotions.length);
  };

  const prevPromo = () => {
    setCurrentPromoIndex((prev) => (prev - 1 + promotions.length) % promotions.length);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <main className="container mx-auto px-4 py-8 mt-16">
        {/* Welcome Section */}
        <div className="mb-8 flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 mb-2">Welcome back!</h1>
            <p className="text-gray-600">Here's what's happening with your account today.</p>
          </div>
          <Button 
            variant="outline" 
            onClick={() => setShowProfileSettings(!showProfileSettings)}
            className="text-sm flex items-center space-x-2"
          >
            <Settings className="h-4 w-4" />
            <span>{showProfileSettings ? 'Hide' : 'Show'} Profile Settings</span>
          </Button>
        </div>

        {/* User Profile Settings Component */}
        {showProfileSettings && (
          <div className="mb-8">
            <UserProfileSettings />
          </div>
        )}

        {/* Balance Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          {balances.map((balance, index) => (
            <Card key={balance.currency} className="relative overflow-hidden">
              <CardContent className="p-6">
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center space-x-2">
                    <span className="text-2xl">{balance.flag}</span>
                    <span className="font-semibold text-gray-700">{balance.currency}</span>
                  </div>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setShowBalance(!showBalance)}
                  >
                    {showBalance ? <Eye className="h-4 w-4" /> : <EyeOff className="h-4 w-4" />}
                  </Button>
                </div>
                <div className="text-2xl font-bold text-gray-900">
                  {showBalance ? balance.amount : '****'}
                </div>
                <p className="text-sm text-gray-500 mt-2">Available Balance</p>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Quick Actions */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle>Quick Actions</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              {quickActions.map((action, index) => (
                <Button
                  key={action.label}
                  variant="outline"
                  className="h-24 flex-col space-y-2 hover:bg-gray-50"
                  onClick={() => window.location.href = action.href}
                >
                  <div className={`p-3 rounded-full ${action.color} text-white`}>
                    <action.icon className="h-6 w-6" />
                  </div>
                  <span className="text-sm font-medium">{action.label}</span>
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Recent Transactions & Savings Goals */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
          {/* Recent Transactions */}
          <Card>
            <CardHeader className="flex flex-row items-center justify-between">
              <CardTitle>Recent Transactions</CardTitle>
              <Button variant="ghost" size="sm">View All</Button>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {recentTransactions.map((transaction) => (
                  <div key={transaction.id} className="flex items-center justify-between p-3 rounded-lg border">
                    <div className="flex items-center space-x-3">
                      <div className={`p-2 rounded-full ${
                        transaction.type === 'sent' ? 'bg-red-100 text-red-600' :
                        transaction.type === 'received' ? 'bg-green-100 text-green-600' :
                        transaction.type === 'bill' ? 'bg-blue-100 text-blue-600' :
                        'bg-orange-100 text-orange-600'
                      }`}>
                        {transaction.type === 'sent' && <ArrowUpRight className="h-4 w-4" />}
                        {transaction.type === 'received' && <ArrowDownRight className="h-4 w-4" />}
                        {transaction.type === 'bill' && <CreditCard className="h-4 w-4" />}
                        {transaction.type === 'airtime' && <Smartphone className="h-4 w-4" />}
                      </div>
                      <div>
                        <p className="font-medium text-sm">{transaction.description}</p>
                        <p className="text-xs text-gray-500">{transaction.date}</p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className={`font-semibold ${
                        transaction.amount.startsWith('+') ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {transaction.amount}
                      </p>
                      <Badge variant="secondary" className="text-xs">
                        {transaction.status}
                      </Badge>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Savings Goals */}
          <Card>
            <CardHeader className="flex flex-row items-center justify-between">
              <CardTitle>Savings Goals</CardTitle>
              <Button variant="ghost" size="sm">Add Goal</Button>
            </CardHeader>
            <CardContent>
              <div className="space-y-6">
                {savingsGoals.map((goal, index) => (
                  <div key={index} className="space-y-3">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        <Target className="h-5 w-5 text-blue-500" />
                        <span className="font-medium">{goal.name}</span>
                      </div>
                      <span className="text-sm text-gray-500">
                        {Math.round((goal.current / goal.target) * 100)}%
                      </span>
                    </div>
                    <Progress 
                      value={(goal.current / goal.target) * 100} 
                      className="h-2"
                    />
                    <div className="flex justify-between text-sm text-gray-600">
                      <span>
                        {goal.currency === 'NGN' ? '₦' : '$'}{goal.current.toLocaleString()}
                      </span>
                      <span>
                        {goal.currency === 'NGN' ? '₦' : '$'}{goal.target.toLocaleString()}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Promotions Carousel */}
        <Card className="mb-8">
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="flex items-center space-x-2">
              <Gift className="h-5 w-5 text-red-500" />
              <span>Special Offers</span>
            </CardTitle>
            <div className="flex space-x-2">
              <Button variant="ghost" size="sm" onClick={prevPromo}>
                <ChevronLeft className="h-4 w-4" />
              </Button>
              <Button variant="ghost" size="sm" onClick={nextPromo}>
                <ChevronRight className="h-4 w-4" />
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <div className={`p-6 rounded-lg text-white ${promotions[currentPromoIndex].color}`}>
              <div className="flex items-start justify-between mb-4">
                <Badge variant="secondary" className="bg-white/20 text-white">
                  {promotions[currentPromoIndex].badge}
                </Badge>
              </div>
              <h3 className="text-xl font-bold mb-2">{promotions[currentPromoIndex].title}</h3>
              <p className="text-white/90 mb-4">{promotions[currentPromoIndex].description}</p>
              <Button variant="secondary" size="sm">
                Learn More
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Profile Overview */}
        <Card>
          <CardHeader>
            <CardTitle>Profile Overview</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="text-center p-4 border rounded-lg">
                <div className="text-2xl font-bold text-blue-600 mb-2">98%</div>
                <p className="text-sm text-gray-600">Profile Completion</p>
              </div>
              <div className="text-center p-4 border rounded-lg">
                <div className="text-2xl font-bold text-green-600 mb-2">Verified</div>
                <p className="text-sm text-gray-600">Account Status</p>
              </div>
              <div className="text-center p-4 border rounded-lg">
                <div className="text-2xl font-bold text-purple-600 mb-2">Gold</div>
                <p className="text-sm text-gray-600">Membership Tier</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </main>

      <Footer />
    </div>
  );
}
</content>
